    setCount(props.targetcount+1)
  }
  console.log(count);
  return (
    <div className='border my-8 mx-8  '>
      <h1>home shop</h1>
      <button className='rounded-none'
       onClick={plus1}>+1</button>
       <div>{count}</div>
    </div>
  );
}

export default HomeShop;
